mapper.update:
UPDATE t_commission_rule
storeId = #{storeId}, commissionType = #{commissionType}, commissionName = #{commissionName}, commissionWay = #{commissionWay}, commissionAmount = #{commissionAmount}, beginDate = #{beginDate}, endDate = #{endDate}, beginTime = #{beginTime}, endTime = #{endTime}, week = #{week}, isDelete = #{isDelete}, creator = #{creator}, updater = #{updater}, ctime = #{ctime}, utime = #{utime}
WHERE id = #{id}

mapper.select:
SELECT 
FROM t_commission_rule


	and storeId = #{storeId}


	and commissionType = #{commissionType}


	and commissionName = #{commissionName}


	and commissionWay = #{commissionWay}


	and commissionAmount = #{commissionAmount}


	and beginDate = #{beginDate}


	and endDate = #{endDate}


	and beginTime = #{beginTime}


	and endTime = #{endTime}


	and week = #{week}


	and isDelete = #{isDelete}


	and creator = #{creator}


	and updater = #{updater}


	and ctime = #{ctime}


	and utime = #{utime}
